  begin
    if SYSRST = '1' then
      SL1 <= '0';
      SL2 <= '0';
      SH1 <= '0';
      SH2 <= '0';  
      COINC_DEL <= '0';        
      BLOCK_COINC_SYNCHR <= '0';        
