        when "000010" => SCINT_COINC <= TR_CONDITION2;
        when "000011" => SCINT_COINC <= TR_CONDITION3;
        when "000100" => SCINT_COINC <= TR_CONDITION4;
        when "001000" => SCINT_COINC <= TR_CONDITION5;
